    private void BotonGuardarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BotonGuardarActionPerformed
   
        // TODO add your handling code here:
        //pedimos los valores de la pregunta
        String titulo=TxtTitulo.getText(); 
        String op1=TxtTitulo.getText();
        String op2=TxtTitulo.getText();
        String op3=TxtTitulo.getText();
        String op4=TxtTitulo.getText();
        // construiremos cada opcion
        Opcion o=new Opcion(op1, true);
        Opcion o2=new Opcion(op2,false);
        Opcion o3=new Opcion(op3,false);
        Opcion o4=new Opcion(op4,false);
        ArrayList<Opcion> Opciones = null;
        
       Pregunta p=new Pregunta(titulo,Opciones);
       try{
           PersitenciaPregunta.guardar(p);
           Txtop1.setText(null);
           Txtop2.setText(null);
           Txtop3.setText(null);
           //Txtop4.setText(null);
           TxtTitulo.setText(null);
           JOptionPane.showConfirmDialog(this,"Pregunta guardarda");
       }catch(Exception ex){
           
       }
    }//GEN-LAST:event_BotonGuardarActionPerformed

